import numpy as np
import networkx as nx
import pygraphviz
import re
import os

def matrixToWeightedNx(matrix,fileList):
    g=nx.DiGraph()
    numRows=len(matrix)
    numCols=len(matrix[0])
    for row in range(numRows):
        for col in range(numCols):
            if row!=col:
                if matrix[row,col]!=0:
                    g.add_edge(fileList[row],fileList[col],weight=matrix[row,col])
    return g

        
